// Extrair dados da mensagem WhatsApp
const data = $input.first().json;

// Formato padrão do WhatsApp Business API
const telefone = data.from || data.phone || '5511999999999';
const mensagem = data.text?.body || data.message || data.text || '';
const nomeContato = data.profile?.name || data.name || 'Cliente';
const timestamp = data.timestamp || new Date().toISOString();

console.log('📱 Mensagem recebida:', {
  telefone,
  mensagem,
  nomeContato,
  timestamp
});

return {
  telefone: telefone.replace(/\D/g, ''), // Limpar número
  mensagem: mensagem.trim(),
  nomeContato,
  timestamp,
  conversaId: `conv_${telefone.replace(/\D/g, '')}_${Date.now()}`
};

// Processar resposta da IA e detectar intenções
const respostaIA = $('ChatGPT - IA Farmácia').first().json.message?.content || $('ChatGPT - IA Farmácia').first().json.text || '';
const dadosCliente = $('Processar WhatsApp').first().json;
const cliente = $('Buscar Cliente').first().json[0] || null;
const produtos = $('Buscar Produtos').first().json || [];

// Detectar se é consulta de preço
const isConsultaPreco = /preço|quanto custa|valor|custo/i.test(dadosCliente.mensagem);

// Detectar produtos mencionados
const produtosMencionados = [];
const produtosBase = ['dipirona', 'paracetamol', 'ibuprofeno', 'omeprazol', 'pantoprazol', 'amoxicilina', 'azitromicina', 'vitamina', 'complexo b', 'metformina'];

produtosBase.forEach(produto => {
  if (dadosCliente.mensagem.toLowerCase().includes(produto)) {
    produtosMencionados.push(produto);
  }
});

// Enriquecer resposta se for consulta de preço
let respostaFinal = respostaIA;

if (isConsultaPreco && produtosMencionados.length > 0) {
  respostaFinal += `\n\n📋 *AÇÕES DISPONÍVEIS:*\n1️⃣ Reservar produto\n2️⃣ Ver bula/informações\n3️⃣ Falar com farmacêutico\n4️⃣ Ver produtos similares\n\n💬 Digite o número da opção ou me diga como posso ajudar!`;
}

// Adicionar saudação personalizada para clientes conhecidos
if (cliente && cliente.nome) {
  respostaFinal = `Olá ${cliente.nome}! 😊\n\n` + respostaFinal;
} else if (!cliente) {
  respostaFinal = `Olá! Bem-vindo à Farmácia São João! 👋\n\n` + respostaFinal;
}

// Adicionar informações de estoque baixo se relevante
const produtosBaixoEstoque = produtos.filter(p => p.estoque_atual <= 5);
if (produtosBaixoEstoque.length > 0 && isConsultaPreco) {
  respostaFinal += `\n\n⚠️ *ATENÇÃO - Estoque limitado:*\n${produtosBaixoEstoque.map(p => `• ${p.nome} - Apenas ${p.estoque_atual} unidades`).join('\n')}`;
}

console.log('🤖 Resposta processada:', {
  cliente: cliente?.nome || 'Novo',
  isConsultaPreco,
  produtosMencionados,
  tamanhoResposta: respostaFinal.length,
  produtosBaixoEstoque: produtosBaixoEstoque.length
});

return {
  telefone: dadosCliente.telefone,
  resposta: respostaFinal,
  isConsultaPreco,
  produtosMencionados,
  cliente: cliente,
  produtosBaixoEstoque,
  metadata: {
    timestamp: new Date().toISOString(),
    modelo: 'gpt-4',
    tokens: respostaIA.length,
    intent: isConsultaPreco ? 'consulta_preco' : 'conversa_geral',
    produtos_encontrados: produtos.length
  }
};

// Preparar resposta final para WhatsApp
const dados = $input.first().json;

// Limitar tamanho da mensagem (WhatsApp tem limite)
let resposta = dados.resposta;
if (resposta.length > 4000) {
  resposta = resposta.substring(0, 3900) + '...\n\n💬 Resposta muito longa! Digite "mais info" para continuar.';
}

// Adicionar assinatura da farmácia
resposta += '\n\n---\n🏥 *Farmácia São João*\n📱 Atendimento 24h via WhatsApp\n📍 Sempre os melhores preços!';

console.log('✅ Resposta final preparada:', {
  telefone: dados.telefone,
  tamanho: resposta.length,
  intent: dados.metadata?.intent,
  produtos_mencionados: dados.produtosMencionados?.length || 0
});

return {
  to: dados.telefone,
  message: resposta,
  success: true,
  timestamp: new Date().toISOString(),
  intent: dados.metadata?.intent,
  produtos_encontrados: dados.metadata?.produtos_encontrados
};
