// === PROCESSAR ENTRADA ===
const input = $input.first().json;
console.log('📥 Input recebido:', input);

// Simular mensagem se não vier do WhatsApp
const mensagem = input.message || input.text || input.body || 'teste';
const telefone = input.phone || input.from || '5511999999999';
const nome = input.name || input.profile?.name || 'Cliente';

// Limpar telefone
const telefoneLimpo = telefone.toString().replace(/\D/g, '');

console.log('✅ Dados processados:', {
  mensagem,
  telefone: telefoneLimpo,
  nome
});

return {
  mensagem: mensagem.trim(),
  telefone: telefoneLimpo,
  nome: nome,
  timestamp: new Date().toISOString(),
  conversaId: `conv_${telefoneLimpo}_${Date.now()}`
};

// === PREPARAR DADOS PARA IA ===
const entrada = $('Processar Entrada').first().json;
const clienteData = $('Buscar Cliente').first().json || [];
const produtosData = $('Buscar Produtos').first().json || [];

console.log('📊 Dados recebidos:', {
  entrada,
  totalClientes: clienteData.length,
  totalProdutos: produtosData.length
});

// Cliente encontrado?
const cliente = clienteData.length > 0 ? clienteData[0] : null;

// Montar lista de produtos
const listaProdutos = produtosData.map(p => 
  `• ${p.nome} ${p.dosagem} - R$ ${p.preco_venda} (${p.estoque_atual} unid)`
).join('\n');

// Context para IA
const context = {
  cliente: cliente,
  produtos: listaProdutos,
  mensagem: entrada.mensagem,
  isNovoCliente: !cliente
};

console.log('🤖 Context preparado para IA');

return context;

// === SIMULAR RESPOSTA INTELIGENTE ===
const context = $input.first().json;
const mensagem = context.mensagem.toLowerCase();

let resposta = '';

// Saudação personalizada
if (context.cliente) {
  resposta += `Olá ${context.cliente.nome}! 😊\n\n`;
} else {
  resposta += `Olá! Bem-vindo à Farmácia São João! 👋\n\n`;
}

// Detectar intenção
if (mensagem.includes('preço') || mensagem.includes('custa') || mensagem.includes('valor')) {
  resposta += `💊 **CONSULTA DE PREÇOS**\n\n`;

  if (mensagem.includes('dipirona')) {
    resposta += `🔍 **Dipirona encontrada:**\n• Dipirona EMS 500mg - R$ 8,50 ✅ 45 unid\n• Dipirona Infantil - R$ 12,50 ✅ 29 unid\n\n`;
  } else if (mensagem.includes('paracetamol')) {
    resposta += `🔍 **Paracetamol encontrado:**\n• Paracetamol Neo Química 750mg - R$ 12,90 ✅ 35 unid\n\n`;
  } else {
    resposta += `🔍 **Produtos disponíveis:**\n${context.produtos}\n\n`;
  }

  resposta += `📊 **Comparação:**\n• Outras farmácias: +15-20% mais caro\n\n`;
  resposta += `💡 **Próximas ações:**\n1️⃣ Reservar produto\n2️⃣ Falar com farmacêutico\n3️⃣ Ver mais opções\n\n`;
} else {
  resposta += `🤖 **Como posso ajudar hoje?**\n\n`;
  resposta += `💊 Digite "preço + nome do medicamento"\n`;
  resposta += `📞 Digite "farmacêutico" para falar conosco\n`;
  resposta += `🏥 Digite "produtos" para ver disponíveis\n\n`;
}

resposta += `---\n🏥 *Farmácia São João*\n📱 Atendimento via WhatsApp\n📍 Sempre os melhores preços!`;

console.log('✅ Resposta gerada:', {
  tamanho: resposta.length,
  temCliente: !!context.cliente
});

return {
  telefone: $('Processar Entrada').first().json.telefone,
  resposta: resposta,
  success: true,
  timestamp: new Date().toISOString()
};
